interface ILisan {
    function getBlockHash(
        uint64 _chainId,
        uint64 _blockNumber
    ) external returns (bytes32);
}

contracts/Lisan.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./circuits/UltraVerifier.sol";
import "./OChain.sol";
import "./ILisan.sol";

// the lisan al gaib will point the way
contract Lisan is ILisan, OChain {
    constructor(address _owner, address _endpoint) OChain(_endpoint, _owner) {}

    function getBlockHash(
        uint64 _chainId,
        uint64 _blockNumber
    ) public view returns (bytes32) {
        return visions[_chainId][_blockNumber];
    }

    function isBlockInHistory(
        uint64 _chainId,
        uint64 _blockNumber
    ) public view returns (bool) {
        return visions[_chainId][_blockNumber] != bytes32(0);
    }

    // handy for testing - this functionality IRL needs to be only callable by OZ
    function addToHistory(
        uint64 _chainId,
        uint64 _blockNumber,
        bytes32 _blockhash
    ) public onlyOwner {
        visions[_chainId][_blockNumber] = _blockhash;
        emit BlockHashReceived(
            uint64(_chainId),
            uint64(_blockNumber),
            _blockhash
        );
    }
}

contracts/OChain.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.22;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {OApp, MessagingFee, Origin} from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OApp.sol";
import {MessagingReceipt} from "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OAppSender.sol";

contract OChain is OApp {
    constructor(
        address _endpoint,
        address _delegate
    ) OApp(_endpoint, _delegate) Ownable(_delegate) {}

    mapping(uint64 chainId => mapping(uint64 timestamp => bytes32 blockhash))
        public visions;

    function getBlockHash(uint blockNumber) public view returns (bytes32) {
        require(
            block.number > blockNumber,
            "Block number should be in the past"
        );
        require(
            block.number - blockNumber <= 256,
            "Block number is too far in the past"
        );

        return blockhash(blockNumber);
    }

    function send(
        uint32 _dstEid,
        uint _blockNumber,
        bytes calldata _options
    ) external payable returns (MessagingReceipt memory receipt) {
        // get the block hash
        bytes32 hashToGet = getBlockHash(_blockNumber);
        require(hashToGet != 0, "Blockhash not found");

        // encode the message
        bytes memory _payload = abi.encode(
            _blockNumber,
            hashToGet,
            block.chainid
        );

        receipt = _lzSend(
            _dstEid,
            _payload,
            _options,
            MessagingFee(msg.value, 0),
            payable(msg.sender)
        );
    }

    function quote(
        uint32 _dstEid,
        uint _blockNumber,
        bytes memory _options,
        bool _payInLzToken
    ) public view returns (MessagingFee memory fee) {
        bytes32 hashToGet = getBlockHash(_blockNumber);
        require(hashToGet != 0, "Blockhash not found");

        bytes memory payload = abi.encode(_blockNumber, hashToGet);

        fee = _quote(_dstEid, payload, _options, _payInLzToken);
    }

    event BlockHashReceived(
        uint64 chainId,
        uint64 blockNumber,
        bytes32 blockHash
    );

    function _lzReceive(
        Origin calldata /*_origin*/,
        bytes32 /*_guid*/,
        bytes calldata payload,
        address /*_executor*/,
        bytes calldata /*_extraData*/
    ) internal override {
        (uint blocknum, bytes32 blockHash, uint chainId) = abi.decode(
            payload,
            (uint, bytes32, uint)
        );
        visions[uint64(chainId)][uint64(blocknum)] = blockHash;
        emit BlockHashReceived(uint64(chainId), uint64(blocknum), blockHash);
    }
}

contracts/ProofOfETHSepoliaTransfer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./circuits/UltraVerifier.sol";
import "./ILisan.sol";

// this is a demo contract that allows a user on polygon amoy, to prove that they
// transacted on ethereum sepolia
// transacted here - refers to a user proving that they sent 2 ETH to address INSERT
contract ProofOfETHSepoliaTransfer {
    UltraVerifier public verifier;
    ILisan public lisan;

    mapping(bytes32 => bool) public txProven;

    constructor(address _verifier, address _lisan) {
        verifier = UltraVerifier(_verifier);
        lisan = ILisan(_lisan);
    }

    function concatenate(
        uint8[32] memory inputArray
    ) public pure returns (bytes32) {
        bytes32 result;

        for (uint i = 0; i < 32; i++) {
            result |= bytes32(uint256(inputArray[i]) << (248 - i * 8));
        }

        return result;
    }

    function convertAndConcatenate(
        bytes32[] memory inputArray
    ) public pure returns (bytes32) {
        uint8[32] memory result;

        for (uint i = 0; i < inputArray.length; i++) {
            uint8 value = uint8(uint256(inputArray[i]));
            result[i] = value;
        }

        bytes32 concat = concatenate(result);

        return concat;
    }

    event SepoliaTxProven(bytes32 sepoliaBlockHash, uint256 txIdx);

    // public inputs is as follows:
    // [chain_id, block_number, tx_index, value, block_hash]
    // block hash is the last 32 elements of the public inputs
    function proveETHSepoliaTransfer(
        bytes calldata _proof,
        bytes32[] calldata _publicInputs
    ) public {
        // noir typings are a bit heinous
        // we need to convert our tx hash in array form to something readable
        // our block hash from the user is the last 32 elements of the public inputs
        bytes32[] memory last32Elements = new bytes32[](32);

        // Copy the last 32 elements from the input array to the new array
        for (uint i = 0; i < 32; i++) {
            last32Elements[i] = _publicInputs[_publicInputs.length - 32 + i];
        }

        bytes32 blockHash = convertAndConcatenate(last32Elements);
        bytes32 lisanBlockHash = lisan.getBlockHash(
            uint64(uint256(_publicInputs[0])),
            uint64(uint256(_publicInputs[1]))
        );

        // check that the amount of the transaction is correct
        require(uint256(_publicInputs[3]) == 2 ether, "incorrect amount!");

        // check that the input block hash matches lisans block hash
        require(lisanBlockHash == blockHash, "Block not in history");

        // check that the users proof is valid
        bool isValid = verifier.verify(_proof, _publicInputs);
        require(isValid, "Invalid proof!");

        // mark the tx as proven hash(block + tx_idx in block) as I don't have the tx hash handy atm
        txProven[
            keccak256(abi.encodePacked(blockHash, _publicInputs[2]))
        ] = true;

        // emit an event to prove that this tx was proven
        emit SepoliaTxProven(blockHash, uint256(_publicInputs[2]));
    }
}
